import React, {useState} from 'react'; //UseState para el estado
import LandingScreen from '../pages/LandingScreen';
import RegisterScreen from '../pages/RegisterScreen';
import LoginScreen from '../pages/LoginScreen';
import DashboardScreen from '../pages/DashboardScreen'; // este está dentro de una subcarpeta


const QuiveApp = () => {
  const [currentScreen, setCurrentScreen] = useState('landing');
  const [userType, setUserType] = useState('');
  const [userData, setUserData] = useState({});
  const [registroExitoso, setRegistroExitoso] = useState(false);

  console.log('Pantalla actual:', currentScreen);

  const getScreen = () => {
    switch (currentScreen) {
      case 'landing':
        return <LandingScreen 
        onNavigate={setCurrentScreen} />;
      case 'register':
        return <RegisterScreen 
        onNavigate={setCurrentScreen} 
        userType={userType} 
        setUserType={setUserType}
        setRegistroExitoso={setRegistroExitoso} />;
      case 'login':
        return <LoginScreen 
        onNavigate={setCurrentScreen} 
        setUserData={setUserData} />;
      case 'dashboard':
        return <DashboardScreen 
        serData={userData} 
        onNavigate={setCurrentScreen} 
        userType={userType} />;
      case 'registroExitoso':
        return <registroExitosoScreen 
        onNavigate={setCurrentScreen}/>;
      default:
        return <LandingScreen onNavigate={setCurrentScreen} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {getScreen()}
    </div>
  );
};
export default QuiveApp; // Exporta el componente QuiveApp para su uso en otros archivos
