import React, { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
} from 'react-router-dom';

import LandingScreen from '../pages/LandingScreen';
import RegisterScreen from '../pages/RegisterScreen';
import LoginScreen from '../pages/LoginScreen';
import RegistroExitosoScreen from '../pages/RegistroExitosoScreen';
import DashboardScreen from '../pages/DashboardScreen';
import DashboardScreenTransportistas from '../pages/DashboardScreenTransportista';

const AppRoutes = () => {
  const navigate = useNavigate();

  const [userType, setUserType] = useState(() => localStorage.getItem('userType') || '');
  const [userData, setUserData] = useState(() => {
  const [registroExitoso, setRegistroExitoso] = useState(false);
    const storedData = localStorage.getItem('userData');
    return storedData ? JSON.parse(storedData) : {};
  });

  useEffect(() => {
    localStorage.setItem('userType', userType);
  }, [userType]);

  useEffect(() => {
    localStorage.setItem('userData', JSON.stringify(userData));
  }, [userData]);

  return (
    <Routes>
      <Route path="/" element={<LandingScreen onNavigate={navigate} />} />
      <Route
        path="/register"
        element={
          <RegisterScreen
            onNavigate={navigate}
            userType={userType}
            setUserType={setUserType}
            setRegistroExitoso={setRegistroExitoso}
          />
        }
      />
      <Route
        path="/login"
        element={<LoginScreen onNavigate={navigate} setUserData={setUserData} />}
      />
      <Route
        path="/registroExitoso"
        element={<RegistroExitosoScreen onNavigate={navigate} />}
      />
      <Route
        path="/dashboard*"
        element={
          userData?.userType === 'transportista' ? (
            <DashboardScreenTransportistas
              userData={userData}
              onNavigate={navigate}
              userType={userType}
            />
          ) : (
            <DashboardScreen
              userData={userData}
              onNavigate={navigate}
              userType={userType}
            />
          )
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

const QuiveApp = () => {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <AppRoutes />
      </div>
    </Router>
  );
};

export default QuiveApp;
