import React, { useState } from 'react';
import { ArrowLeft, MapPin, Calendar, Clock, Plus, Edit, Star, Check, CreditCard, X } from 'lucide-react';

const MudanzaFlow = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    origen: '',
    destino: '',
    fecha: '',
    hora: '',
    objetos: [],
    conductor: null,
    notas: ''
  });

  const [nuevoObjeto, setNuevoObjeto] = useState({
    tipo: 'Cocina',
    cantidad: 1,
    descripcion: '',
    altura: '',
    ancho: '',
    profundidad: '',
    fragil: false
  });

  const [selectedPayment, setSelectedPayment] = useState('card');
  const [selectedDriver, setSelectedDriver] = useState(null);

  const nextStep = () => {
    if (currentStep < 5) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const agregarObjeto = () => {
    if (nuevoObjeto.descripcion.trim() === '') {
      alert('Por favor, ingrese una descripción para el objeto');
      return;
    }
    
    setFormData({
      ...formData,
      objetos: [...formData.objetos, { ...nuevoObjeto, id: Date.now() }]
    });
    setNuevoObjeto({
      tipo: 'Cocina',
      cantidad: 1,
      descripcion: '',
      altura: '',
      ancho: '',
      profundidad: '',
      fragil: false
    });
  };

  const eliminarObjeto = (id) => {
    setFormData({
      ...formData,
      objetos: formData.objetos.filter(obj => obj.id !== id)
    });
  };

  const seleccionarConductor = (conductor) => {
    setSelectedDriver(conductor);
    setFormData({
      ...formData,
      conductor: conductor
    });
  };

  const StepIndicator = () => (
    <div className="flex items-center justify-center mb-8">
      {[1, 2, 3, 4, 5].map((step) => (
        <React.Fragment key={step}>
          <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium transition-colors ${
            step <= currentStep ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-600'
          }`}>
            {step <= currentStep ? <Check className="w-4 h-4" /> : step}
          </div>
          {step < 5 && (
            <div className={`w-12 h-0.5 transition-colors ${step < currentStep ? 'bg-blue-500' : 'bg-gray-300'}`} />
          )}
        </React.Fragment>
      ))}
    </div>
  );

  const Header = () => (
    <div className="bg-blue-100 p-4 flex items-center justify-between shadow-sm">
      <div className="flex items-center">
        {currentStep > 1 && (
          <button onClick={prevStep} className="mr-4 hover:bg-blue-200 p-1 rounded transition-colors">
            <ArrowLeft className="w-6 h-6 text-blue-600" />
          </button>
        )}
        <span className="text-blue-600 font-medium">
          {currentStep > 1 ? 'Volver' : ''}
        </span>
      </div>
      <h1 className="text-2xl font-bold text-blue-600">QUIVE</h1>
      <div className="flex space-x-2">
        <div className="w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center hover:bg-blue-700 cursor-pointer transition-colors">
          <span className="text-white text-sm">🔔</span>
        </div>
