        { icon: '📍', label: 'SEGUIMIENTO', active: false },
        { icon: '👤', label: 'PERFIL', active: false }
      ].map((item, index) => (
        <div key={index} className="flex flex-col items-center cursor-pointer hover:opacity-75 transition-opacity">
          <div className={`w-8 h-8 rounded-lg flex items-center justify-center mb-1 ${
            item.active ? 'bg-blue-600' : 'bg-gray-300'
