                  value={nuevoObjeto.profundidad}
                  onChange={(e) => setNuevoObjeto({...nuevoObjeto, profundidad: e.target.value})}
                />
              </div>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">¿Es frágil?</label>
              <div className="flex space-x-4">
                <button
                  type="button"
