import React, { useState } from "react";

// ðŸŽ¨ Estilos como variables
const styles = {
  container: {
    marginBottom: "2rem",
  },
  toggleButton: {
    padding: "12px 16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    backgroundColor: "#f8f8f8",
    cursor: "pointer",
    width: "100%",
    textAlign: "left",
    fontSize: "14px",
    fontWeight: "500",
  },
  row: {
    display: "flex",
    gap: "1rem",
    marginBottom: "1rem",
    flexWrap: "wrap",
  },
  field: {
    flex: 1,
    minWidth: "200px",
  },
  label: {
    fontWeight: "bold",
    marginBottom: "5px",
    display: "block",
  },
  input: {
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    width: "100%",
    fontSize: "14px",
    boxSizing: "border-box",
  },
  fileInfo: {
    fontStyle: "italic",
    fontSize: "14px",
    color: "#333",
    marginTop: "0.5rem",
  },
};

const SubidaDocumentos = ({ documentos, setDocumentos }) => {
  const [mostrarFormulario, setMostrarFormulario] = useState(false);

  const resumen =
    documentos.licencia_conducir?.name &&
    documentos.tarjeta_propiedad?.name &&
    documentos.certificado_itv?.name
      ? "ðŸ“ Documentos seleccionados"
      : "ðŸ“ Sube tus documentos";

  const handleFileChange = (e, tipo) => {
    const file = e.target.files[0];
    if (file) {
      setDocumentos({ ...documentos, [tipo]: file });
    }
  };

  return (
    <div style={styles.container}>
      <button
        type="button"
        style={styles.toggleButton}
        onClick={() => setMostrarFormulario(!mostrarFormulario)}
      >
        {resumen}
      </button>

      {mostrarFormulario && (
        <>
          <div style={styles.row}>
            <div style={styles.field}>
              <label style={styles.label}>Licencia de Conducir:</label>
              <input
                type="file"
                style={styles.input}
                onChange={(e) => handleFileChange(e, "licencia_conducir")}
                accept=".pdf,.jpg,.jpeg,.png"
              />
              {documentos.licencia_conducir && (
                <div style={styles.fileInfo}>
                  {documentos.licencia_conducir.name} ({(documentos.licencia_conducir.size / 1024).toFixed(2)} KB)
                </div>
              )}
            </div>

            <div style={styles.field}>
              <label style={styles.label}>Tarjeta de Propiedad:</label>
              <input
                type="file"
                style={styles.input}
                onChange={(e) => handleFileChange(e, "tarjeta_propiedad")}
                accept=".pdf,.jpg,.jpeg,.png"
              />
              {documentos.tarjeta_propiedad && (
                <div style={styles.fileInfo}>
                  {documentos.tarjeta_propiedad.name} ({(documentos.tarjeta_propiedad.size / 1024).toFixed(2)} KB)
                </div>
              )}
            </div>

            <div style={styles.field}>
              <label style={styles.label}>Certificado de ITV:</label>
              <input
                type="file"
                style={styles.input}
                onChange={(e) => handleFileChange(e, "certificado_itv")}
                accept=".pdf,.jpg,.jpeg,.png"
              />
              {documentos.certificado_itv && (
                <div style={styles.fileInfo}>
                  {documentos.certificado_itv.name} ({(documentos.certificado_itv.size / 1024).toFixed(2)} KB)
                </div>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default SubidaDocumentos;
