import React, {useState} from 'react';
import {ArrowLeft} from 'lucide-react';

const RegisterScreen = ({onNavigate, userType, setUserType, setRegistroExitoso}) => {
    console.log('setUserType:', setUserType); 
  
    const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    telefono: '',
    dni: '',
    password: '',
    confirmPassword: '',
    placa: '',
    tipoVehiculo: ''
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    // Clear error when user starts typing
    if (errors[e.target.name]) {
      setErrors({ ...errors, [e.target.name]: '' });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nombre.trim()) newErrors.nombre = 'El nombre es requerido';
    if (!formData.email.trim()) newErrors.email = 'El email es requerido';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email inválido';
    if (!formData.telefono.trim()) newErrors.telefono = 'El teléfono es requerido';
    if (!formData.dni.trim()) newErrors.dni = 'El DNI es requerido';
    if (!formData.password) newErrors.password = 'La contraseña es requerida';
    else if (formData.password.length < 6) newErrors.password = 'La contraseña debe tener al menos 6 caracteres';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Las contraseñas no coinciden';
    if (!userType) newErrors.userType = 'Selecciona un tipo de usuario';

    if (userType === 'transportista') {
      if (!formData.placa.trim()) newErrors.placa = 'La placa es requerida';
      if (!formData.tipoVehiculo.trim()) newErrors.tipoVehiculo = 'El tipo de vehículo es requerido';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    // Construir payload para la API
    const payload = {
      nombre_completo: formData.nombre,
      email: formData.email,
      telefono: formData.telefono,
      dni: parseInt(formData.dni),
      contrasena: formData.password,
      tipo_usuario: userType,
      ubicacion: "Lima, Perú", // Puedes ponerlo como un campo opcional en tu formulario
      foto_perfil_url: "https://via.placeholder.com/150" // Por defecto, si no hay input
    };

    try {
      const response = await fetch("http://127.0.0.1:5000/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(payload)
      });

      const data = await response.json();

      if (!response.ok) {
        // Si hubo errores de validación desde el backend
        if (data.errors) {
          console.error("Errores de validación del backend:", data.errors);
          setErrors(data.errors);
        } else if (data.msg) {
          alert(data.msg);
        } else {
          alert("Ocurrió un error al registrar.");
        }
        return;
      }

      console.log("Registro exitoso:", data);
      setRegistroExitoso(true);
      onNavigate('registroExitoso');
    } catch (error) {
      console.error("Error al conectar con la API:", error);
      alert("Error de red o servidor.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-white shadow-sm p-6 flex items-center">
        <button 
          onClick={() => onNavigate('landing')}
          className="mr-4 p-2 text-gray-600 hover:text-blue-600 transition-colors"
        >
          <ArrowLeft size={24} />
        </button>
        <div className="text-2xl font-bold text-blue-600">QUIVE</div>
      </header>

      {/* Main Content */}
      <div className="flex items-center justify-center min-h-screen py-12">
        <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
          <div className="text-center mb-8">
            <p className="text-gray-600 mb-2">Bienvenido a QUIVE</p>
            <h1 className="text-3xl font-bold text-blue-600 mb-6">CREAR CUENTA</h1>
            
            {/* User Type Toggle */}
            <div className="flex bg-blue-50 rounded-full p-1 mb-8">
              <button
                type="button"
                onClick={() => setUserType('cliente')}
                className={`flex-1 py-3 px-6 rounded-full transition-colors ${
                  userType === 'cliente' 
                    ? 'bg-blue text-blue-600 shadow-sm' 
                    : 'text-blue-400 hover:text-blue-600'
                }`}
              >
                CLIENTE
              </button>
              <button
                type="button"
                onClick={() => setUserType('transportista')}
                className={`flex-1 py-3 px-6 rounded-full transition-colors ${
                  userType === 'transportista' 
                    ? 'bg-blue-600 text-white shadow-sm' 
                    : 'text-blue-400 hover:text-blue-600'
                }`}
              >
                TRANSPORTISTA
              </button>
            </div>
            {errors.userType && <p className="text-red-500 text-sm mb-4">{errors.userType}</p>}
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <input
                type="text"
                name="nombre"
                placeholder="Nombre y Apellidos"
                value={formData.nombre}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.nombre ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.nombre && <p className="text-red-500 text-sm mt-1">{errors.nombre}</p>}
            </div>
            
            <div className="grid grid-cols-2 gap-4">
              <div>
                <input
                  type="email"
                  name="email"
                  placeholder="Correo Electrónico"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.email ? 'border-red-500' : 'border-gray-200'
                  }`}
                />
                {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
              </div>
              <div>
                <input
                  type="tel"
                  name="telefono"
                  placeholder="Teléfono"
                  value={formData.telefono}
                  onChange={handleInputChange}
                  className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.telefono ? 'border-red-500' : 'border-gray-200'
                  }`}
                />
                {errors.telefono && <p className="text-red-500 text-sm mt-1">{errors.telefono}</p>}
              </div>
            </div>

            <div>
              <input
                type="text"
                name="dni"
                placeholder="DNI"
                value={formData.dni}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.dni ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.dni && <p className="text-red-500 text-sm mt-1">{errors.dni}</p>}
            </div>

            <div>
              <input
                type="password"
                name="password"
                placeholder="Contraseña"
                value={formData.password}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.password ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
            </div>

            <div>
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirmar Contraseña"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.confirmPassword ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.confirmPassword && <p className="text-red-500 text-sm mt-1">{errors.confirmPassword}</p>}
            </div>

            {userType === 'transportista' && (
              <>
                <div>
                  <input
                    type="text"
                    name="placa"
                    placeholder="Placa de Vehículo"
                    value={formData.placa}
                    onChange={handleInputChange}
                    className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      errors.placa ? 'border-red-500' : 'border-gray-200'
                    }`}
                  />
                  {errors.placa && <p className="text-red-500 text-sm mt-1">{errors.placa}</p>}
                </div>
                <div>
                  <select
                    name="tipoVehiculo"
                    value={formData.tipoVehiculo}
                    onChange={handleInputChange}
                    className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      errors.tipoVehiculo ? 'border-red-500' : 'border-gray-200'
                    }`}
                  >
                    <option value="">Selecciona tipo de vehículo</option>
                    <option value="camioneta">Camioneta</option>
                    <option value="camion-pequeno">Camión Pequeño</option>
                    <option value="camion-mediano">Camión Mediano</option>
                    <option value="camion-grande">Camión Grande</option>
                  </select>
                  {errors.tipoVehiculo && <p className="text-red-500 text-sm mt-1">{errors.tipoVehiculo}</p>}
                </div>
              </>
            )}

            <button
              type="submit"
              className="w-full py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-semibold"
            >
              REGISTRARSE
            </button>
          </form>

          <div className="text-center mt-6">
            <p className="text-gray-600">¿Ya tienes una cuenta?</p>
            <button
              onClick={() => onNavigate('login')}
              className="text-blue-600 hover:text-blue-800 font-semibold transition-colors"
            >
              Iniciar Sesión
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterScreen;
