import React, { useState, useEffect } from 'react';

const DriveImageViewer = ({ fileId }) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [imageSrc, setImageSrc] = useState('');

  useEffect(() => {
    if (fileId) {
      // Usamos directamente el endpoint de descarga:
      const url = `https://drive.usercontent.google.com/download?id=${fileId}&export=view&authuser=0`;
      setImageSrc(url);
    }
  }, [fileId]);

  return (
    <div className="flex justify-center items-center">
      {!isLoaded && <p>Cargando imagen...</p>}
      {imageSrc && (
        <img
          src={imageSrc}
          alt="Imagen de Drive"
          onLoad={() => setIsLoaded(true)}
          className={isLoaded ? 'block max-w-full rounded-lg' : 'hidden'}
        />
      )}
    </div>
  );
};

export default DriveImageViewer;
