import React, { useState } from 'react';
import { Home, Package, Navigation, User, ArrowLeft, Truck, Shield, Clock, MapPin } from 'lucide-react';

const QuiveApp = () => {
  const [currentScreen, setCurrentScreen] = useState('landing');
  const [userType, setUserType] = useState('');
  const [userData, setUserData] = useState({});

  const screens = {
    landing: <LandingScreen onNavigate={setCurrentScreen} />,
    register: <RegisterScreen onNavigate={setCurrentScreen} userType={userType} setUserType={setUserType} />,
    login: <LoginScreen onNavigate={setCurrentScreen} setUserData={setUserData} />,
    dashboard: <DashboardScreen userData={userData} onNavigate={setCurrentScreen} userType={userType} />
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {screens[currentScreen]}
    </div>
  );
};

// Landing Screen
const LandingScreen = ({ onNavigate }) => (
  <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
    {/* Header */}
    <header className="flex justify-between items-center p-6">
      <div className="text-2xl font-bold text-blue-600">QUIVE</div>
      <nav className="hidden md:flex space-x-8">
        <a href="#" className="text-gray-600 hover:text-blue-600 transition-colors">Nosotros</a>
        <a href="#" className="text-gray-600 hover:text-blue-600 transition-colors">Servicios</a>
        <a href="#" className="text-gray-600 hover:text-blue-600 transition-colors">Contacto</a>
      </nav>
      <div className="flex space-x-4">
        <button 
          onClick={() => onNavigate('login')}
          className="px-6 py-2 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors"
        >
          Iniciar Sesión
        </button>
        <button 
          onClick={() => onNavigate('register')}
          className="px-6 py-2 text-blue-600 border border-blue-600 rounded-full hover:bg-blue-600 hover:text-white transition-colors"
        >
          Registrarse
        </button>
      </div>
    </header>

    {/* Main Content */}
    <div className="container mx-auto px-6 py-20">
      <div className="max-w-4xl">
        <h1 className="text-6xl font-bold text-blue-600 mb-8 leading-tight">
          QUIVE, UNA<br />
          MUDANZA SIN<br />
          ESTRÉS
        </h1>
        <p className="text-xl text-gray-700 mb-12 max-w-md">
          Somos tu solución a una mudanza rápida y segura. Conectamos usuarios con transportistas confiables.
        </p>
        <button 
          onClick={() => onNavigate('register')}
          className="px-8 py-4 bg-blue-600 text-white text-lg rounded-lg hover:bg-blue-700 transition-colors shadow-lg hover:shadow-xl"
        >
          INICIAR UNA MUDANZA
        </button>
      </div>
    </div>

    {/* Features Section */}
    <div className="container mx-auto px-6 py-20">
      <h2 className="text-3xl font-bold text-center text-blue-600 mb-12">¿Por qué elegir QUIVE?</h2>
      <div className="grid md:grid-cols-3 gap-8">
        <div className="text-center">
          <div className="bg-blue-600 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
            <Truck className="text-white" size={32} />
          </div>
          <h3 className="text-xl font-semibold text-blue-600 mb-2">Transportistas Verificados</h3>
          <p className="text-gray-600">Todos nuestros transportistas están verificados y cuentan con seguro</p>
        </div>
        <div className="text-center">
          <div className="bg-blue-600 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
            <Clock className="text-white" size={32} />
          </div>
          <h3 className="text-xl font-semibold text-blue-600 mb-2">Servicio Rápido</h3>
          <p className="text-gray-600">Encuentra transportistas disponibles en minutos</p>
        </div>
        <div className="text-center">
          <div className="bg-blue-600 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
            <Shield className="text-white" size={32} />
          </div>
          <h3 className="text-xl font-semibold text-blue-600 mb-2">Seguro y Confiable</h3>
          <p className="text-gray-600">Tu mudanza está protegida con nuestro seguro integral</p>
        </div>
      </div>
    </div>
  </div>
);

// Register Screen
const RegisterScreen = ({ onNavigate, userType, setUserType }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    telefono: '',
    dni: '',
    password: '',
    confirmPassword: '',
    placa: '',
    tipoVehiculo: ''
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    // Clear error when user starts typing
    if (errors[e.target.name]) {
      setErrors({ ...errors, [e.target.name]: '' });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nombre.trim()) newErrors.nombre = 'El nombre es requerido';
    if (!formData.email.trim()) newErrors.email = 'El email es requerido';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email inválido';
    if (!formData.telefono.trim()) newErrors.telefono = 'El teléfono es requerido';
    if (!formData.dni.trim()) newErrors.dni = 'El DNI es requerido';
    if (!formData.password) newErrors.password = 'La contraseña es requerida';
    else if (formData.password.length < 6) newErrors.password = 'La contraseña debe tener al menos 6 caracteres';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Las contraseñas no coinciden';
    if (!userType) newErrors.userType = 'Selecciona un tipo de usuario';

    if (userType === 'transportista') {
      if (!formData.placa.trim()) newErrors.placa = 'La placa es requerida';
      if (!formData.tipoVehiculo.trim()) newErrors.tipoVehiculo = 'El tipo de vehículo es requerido';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Registro exitoso:', formData, userType);
      onNavigate('dashboard');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-white shadow-sm p-6 flex items-center">
        <button 
          onClick={() => onNavigate('landing')}
          className="mr-4 p-2 text-gray-600 hover:text-blue-600 transition-colors"
        >
          <ArrowLeft size={24} />
        </button>
        <div className="text-2xl font-bold text-blue-600">QUIVE</div>
      </header>

      {/* Main Content */}
      <div className="flex items-center justify-center min-h-screen py-12">
        <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
          <div className="text-center mb-8">
            <p className="text-gray-600 mb-2">Bienvenido a QUIVE</p>
            <h1 className="text-3xl font-bold text-blue-600 mb-6">CREAR CUENTA</h1>
            
            {/* User Type Toggle */}
            <div className="flex bg-blue-50 rounded-full p-1 mb-8">
              <button
                type="button"
                onClick={() => setUserType('usuario')}
                className={`flex-1 py-3 px-6 rounded-full transition-colors ${
                  userType === 'usuario' 
                    ? 'bg-white text-blue-600 shadow-sm' 
                    : 'text-blue-400 hover:text-blue-600'
                }`}
              >
                USUARIO
              </button>
              <button
                type="button"
                onClick={() => setUserType('transportista')}
                className={`flex-1 py-3 px-6 rounded-full transition-colors ${
                  userType === 'transportista' 
                    ? 'bg-blue-600 text-white shadow-sm' 
                    : 'text-blue-400 hover:text-blue-600'
                }`}
              >
                TRANSPORTISTA
              </button>
            </div>
            {errors.userType && <p className="text-red-500 text-sm mb-4">{errors.userType}</p>}
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <input
                type="text"
                name="nombre"
                placeholder="Nombre y Apellidos"
                value={formData.nombre}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.nombre ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.nombre && <p className="text-red-500 text-sm mt-1">{errors.nombre}</p>}
            </div>
            
            <div className="grid grid-cols-2 gap-4">
              <div>
                <input
                  type="email"
                  name="email"
                  placeholder="Correo Electrónico"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.email ? 'border-red-500' : 'border-gray-200'
                  }`}
                />
                {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
              </div>
              <div>
                <input
                  type="tel"
                  name="telefono"
                  placeholder="Teléfono"
                  value={formData.telefono}
                  onChange={handleInputChange}
                  className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.telefono ? 'border-red-500' : 'border-gray-200'
                  }`}
                />
                {errors.telefono && <p className="text-red-500 text-sm mt-1">{errors.telefono}</p>}
              </div>
            </div>

            <div>
              <input
                type="text"
                name="dni"
                placeholder="DNI"
                value={formData.dni}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.dni ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.dni && <p className="text-red-500 text-sm mt-1">{errors.dni}</p>}
            </div>

            <div>
              <input
                type="password"
                name="password"
                placeholder="Contraseña"
                value={formData.password}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.password ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
            </div>

            <div>
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirmar Contraseña"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.confirmPassword ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.confirmPassword && <p className="text-red-500 text-sm mt-1">{errors.confirmPassword}</p>}
            </div>

            {userType === 'transportista' && (
              <>
                <div>
                  <input
                    type="text"
                    name="placa"
                    placeholder="Placa de Vehículo"
                    value={formData.placa}
                    onChange={handleInputChange}
                    className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      errors.placa ? 'border-red-500' : 'border-gray-200'
                    }`}
                  />
                  {errors.placa && <p className="text-red-500 text-sm mt-1">{errors.placa}</p>}
                </div>
                <div>
                  <select
                    name="tipoVehiculo"
                    value={formData.tipoVehiculo}
                    onChange={handleInputChange}
                    className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      errors.tipoVehiculo ? 'border-red-500' : 'border-gray-200'
                    }`}
                  >
                    <option value="">Selecciona tipo de vehículo</option>
                    <option value="camioneta">Camioneta</option>
                    <option value="camion-pequeno">Camión Pequeño</option>
                    <option value="camion-mediano">Camión Mediano</option>
                    <option value="camion-grande">Camión Grande</option>
                  </select>
                  {errors.tipoVehiculo && <p className="text-red-500 text-sm mt-1">{errors.tipoVehiculo}</p>}
                </div>
              </>
            )}

            <button
              type="submit"
              className="w-full py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-semibold"
            >
              REGISTRARSE
            </button>
          </form>

          <div className="text-center mt-6">
            <p className="text-gray-600">¿Ya tienes una cuenta?</p>
            <button
              onClick={() => onNavigate('login')}
              className="text-blue-600 hover:text-blue-800 font-semibold transition-colors"
            >
              Iniciar Sesión
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Login Screen
const LoginScreen = ({ onNavigate, setUserData }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (errors[e.target.name]) {
      setErrors({ ...errors, [e.target.name]: '' });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.email.trim()) newErrors.email = 'El email es requerido';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email inválido';
    if (!formData.password) newErrors.password = 'La contraseña es requerida';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      setIsLoading(true);
      // Simular login
      setTimeout(() => {
        setUserData({ 
          name: 'Albert Cavani', 
          email: formData.email,
          userType: 'usuario'
        });
        setIsLoading(false);
        onNavigate('dashboard');
      }, 1000);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-white shadow-sm p-6 flex items-center">
        <button 
          onClick={() => onNavigate('landing')}
          className="mr-4 p-2 text-gray-600 hover:text-blue-600 transition-colors"
        >
          <ArrowLeft size={24} />
        </button>
        <div className="text-2xl font-bold text-blue-600">QUIVE</div>
      </header>

      {/* Main Content */}
      <div className="flex items-center justify-center min-h-screen py-12">
        <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
          <div className="text-center mb-8">
            <p className="text-gray-600 mb-2">Te damos la bienvenida</p>
            <h1 className="text-3xl font-bold text-blue-600 mb-6">INICIAR SESIÓN</h1>
          </div>

          <button className="w-full flex items-center justify-center px-4 py-3 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors mb-6">
            <div className="w-5 h-5 mr-3">
              <svg viewBox="0 0 24 24">
                <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
            </div>
            <span className="text-gray-700">Iniciar Sesión con Google</span>
          </button>

          <div className="text-center text-gray-500 mb-6">
            <span>o</span>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-blue-600 text-sm font-medium mb-2">
                Correo Electrónico
              </label>
              <input
                type="email"
                name="email"
                placeholder="tu@email.com"
                value={formData.email}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.email ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
            </div>

            <div>
              <label className="block text-blue-600 text-sm font-medium mb-2">
                Contraseña
              </label>
              <input
                type="password"
                name="password"
                placeholder="••••••••"
                value={formData.password}
                onChange={handleInputChange}
                className={`w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.password ? 'border-red-500' : 'border-gray-200'
                }`}
              />
              {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
            </div>

            <div className="text-right">
              <button type="button" className="text-blue-600 hover:text-blue-800 text-sm transition-colors">
                ¿Olvidaste tu contraseña?
              </button>
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className="w-full py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-semibold disabled:opacity-50"
            >
              {isLoading ? 'Iniciando sesión...' : 'INICIAR SESIÓN'}
            </button>
          </form>

          <div className="text-center mt-6">
            <p className="text-gray-600">¿No tienes una cuenta?</p>
            <button
              onClick={() => onNavigate('register')}
              className="text-blue-600 hover:text-blue-800 font-semibold transition-colors"
            >
              Crear Cuenta
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Dashboard Screen
const DashboardScreen = ({ userData, onNavigate, userType }) => {
  const [activeTab, setActiveTab] = useState('inicio');

  const renderContent = () => {
    switch (activeTab) {
      case 'inicio':
        return <InicioTab userData={userData} userType={userType} />;
      case 'pedidos':
        return <PedidosTab userType={userType} />;
      case 'seguimiento':
        return <SeguimientoTab />;
      case 'perfil':
        return <PerfilTab userData={userData} onNavigate={onNavigate} />;
      default:
        return <InicioTab userData={userData} userType={userType} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 pb-20">
      {/* Header */}
      <header className="bg-white shadow-sm p-4 flex justify-between items-center">
        <div className="text-2xl font-bold text-blue-600">QUIVE</div>
        <div className="flex space-x-4">
          <button className="p-2 text-gray-600 hover:text-blue-600 transition-colors">
            <User size={24} />
          </button>
        </div>
      </header>

      {/* Main Content */}
      <div className="p-6">
        {renderContent()}
      </div>

      {/* Bottom Navigation */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 shadow-lg">
        <div className="flex justify-around py-2">
          <button
            onClick={() => setActiveTab('inicio')}
            className={`flex flex-col items-center py-3 px-4 ${
              activeTab === 'inicio' ? 'text-blue-600 bg-blue-50' : 'text-gray-600'
            } rounded-lg transition-colors`}
          >
            <Home size={24} />
            <span className="text-xs mt-1 font-semibold">INICIO</span>
          </button>
          
          <button
            onClick={() => setActiveTab('pedidos')}
            className={`flex flex-col items-center py-3 px-4 ${
              activeTab === 'pedidos' ? 'text-blue-600 bg-blue-50' : 'text-gray-600'
            } rounded-lg transition-colors`}
          >
            <Package size={24} />
            <span className="text-xs mt-1 font-semibold">PEDIDOS</span>
          </button>
          
          <button
            onClick={() => setActiveTab('seguimiento')}
            className={`flex flex-col items-center py-3 px-4 ${
              activeTab === 'seguimiento' ? 'text-blue-600 bg-blue-50' : 'text-gray-600'
            } rounded-lg transition-colors`}
          >
            <Navigation size={24} />
            <span className="text-xs mt-1 font-semibold">SEGUIMIENTO</span>
          </button>
          
          <button
            onClick={() => setActiveTab('perfil')}
            className={`flex flex-col items-center py-3 px-4 ${
              activeTab === 'perfil' ? 'text-blue-600 bg-blue-50' : 'text-gray-600'
            } rounded-lg transition-colors`}
          >
            <User size={24} />
            <span className="text-xs mt-1 font-semibold">PERFIL</span>
          </button>
        </div>
      </div>
    </div>
  );
};

