import React from "react";

function ImagenPerfil({ fotoUrl }) {
  if (!fotoUrl) return <p>No hay imagen de perfil</p>;

  // Convertir ?dl=0 a ?raw=1 para mostrar directamente la imagen
  const imagenUrl = fotoUrl
  .replace("www.dropbox.com", "dl.dropboxusercontent.com")
  .replace("?dl=0", "");

  return (
    <div className="rounded-full overflow-hidden w-32 h-32 border border-gray-300">
      <img
        src={imagenUrl}
        alt="Foto de perfil"
        className="object-cover w-full h-full"
      />
    </div>
  );
}

export default ImagenPerfil;
