import React, { useState } from 'react';

const EditarPerfilTab = ({ userData, onGuardar }) => {
  const [formData, setFormData] = useState({
    nombre: userData.name || '',
    email: userData.email || '',
    telefono: userData.telefono || '',
    dni: userData.dni || '',
    password: '',
    tipo_usuario: userData.tipo_usuario || '',
    ubicacion: userData.ubicacion || '',
    fotoPerfil: userData.foto_perfil_url || '',
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = () => {
    const formPayload = new FormData();
    formPayload.append("nombre_completo", formData.nombre);
    formPayload.append("email", formData.email);
    formPayload.append("telefono", formData.telefono);
    formPayload.append("dni", parseInt(formData.dni));
    formPayload.append("contrasena", formData.password);
    formPayload.append("tipo_usuario", formData.tipo_usuario);
    formPayload.append("ubicacion", formData.ubicacion);
    formPayload.append("foto_perfil_url", formData.fotoPerfil || "null");

    onGuardar(formPayload);
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md space-y-4">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Editar Perfil</h2>

      <input name="nombre" value={formData.nombre} onChange={handleChange} className="w-full border rounded p-2" placeholder="Nombre completo" />
      <input name="email" value={formData.email} onChange={handleChange} className="w-full border rounded p-2" placeholder="Correo electrónico" />
      <input name="telefono" value={formData.telefono} onChange={handleChange} className="w-full border rounded p-2" placeholder="Teléfono" />
      <input name="dni" value={formData.dni} onChange={handleChange} className="w-full border rounded p-2" placeholder="DNI" />
      <input name="password" type="password" value={formData.password} onChange={handleChange} className="w-full border rounded p-2" placeholder="Contraseña" />
      <input name="tipo_usuario" value={formData.tipo_usuario} onChange={handleChange} className="w-full border rounded p-2" placeholder="Tipo de Usuario" />
      <input name="ubicacion" value={formData.ubicacion} onChange={handleChange} className="w-full border rounded p-2" placeholder="Ubicación" />
      <input name="fotoPerfil" value={formData.fotoPerfil} onChange={handleChange} className="w-full border rounded p-2" placeholder="URL Foto de Perfil" />

      <button onClick={handleSubmit} className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700">
        Guardar Cambios
      </button>
    </div>
  );
};

export default EditarPerfilTab;
