import React, {useState} from 'react'; //UseState para el estado
import LandingScreen from '../pages/LandingScreen';
import RegisterScreen from '../pages/RegisterScreen';
import LoginScreen from '../pages/LoginScreen';
import DashboardScreen from '../pages/DashboardScreen'; // este está dentro de una subcarpeta


const QuiveApp = () => {
    const [currentScreen, setCurrentScreen] = useState('landing'); // Estado para la pantalla actual
    const [userType, setUserType] = useState(''); // Estado para el tipo de usuario
    const [userData, setUserData] = useState({}); // Estado para los datos del usuario

    const screens = {
        landing: <LandingScreen onNavigate={setCurrentScreen} />, // Pantalla de inicio
        register: <RegisterScreen onNavigate={setCurrentScreen} userType={userType}/>, // Pantalla de registro
        login : <LoginScreen onNavigate ={setCurrentScreen} setUserData={setUserData}/>, // Pantalla de inicio de sesión
        dashboard:<DashboardScreen userData = {userData} onNavigate={setCurrentScreen} userType={userType}/> // Pantalla del dashboard
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {screens[currentScreen]} {/* Renderiza la pantalla actual */}
        </div>
    );
};
console.log('Pantalla actual:', currentScreen);
export default QuiveApp; // Exporta el componente QuiveApp para su uso en otros archivos
